mapper.select:
select

from t_crm_trade

    where tradeNo like CONCAT('%','${param1.search}','%' )

order by ${param1.order} ${param1.dir}
limit #{param2},#{param3}

SELECT tra.* FROM t_crm_trade tra INNER JOIN r_dept_trade rdt ON rdt.tradeid = tra.tradeId WHERE rdt.deptid = '${param1.deptid}'
AND tra.tradeNo like CONCAT('%','${param1.search}','%' )
order by ${param1.order} ${param1.dir} limit #{param2},#{param3}
