mapper.select:
select

from t_bd_user
where username = #{0}
and password = #{1}

select * from t_bd_user where username != "Admin"

    and username like CONCAT('%','${param1.search}','%' )
    or nickname like CONCAT('%','${param1.search}','%' )

order by ${param1.order} ${param1.dir}
limit #{param2},#{param3}

SELECT user.* FROM t_bd_user user INNER JOIN r_dept_user rdu ON rdu.userid = user.userid WHERE user.username != 'Admin' and rdu.deptid='${param1.deptid}'
and user.username like CONCAT('%','${param1.search}','%' ) or user.nickname like CONCAT('%','${param1.search}','%' )
order by ${param1.order} ${param1.dir} limit #{param2},#{param3}
